  `introduction` varchar(255) DEFAULT NULL COMMENT '简介',
  `info` text COMMENT '内容',
  `state` int(11) DEFAULT NULL COMMENT '状态',
  `articletype` varchar(255) DEFAULT NULL COMMENT '文章属性,分咨询，文章，素材',
  `fristtime` varchar(255) DEFAULT NULL COMMENT '最开始时间',
  `endtime` varchar(255) DEFAULT NULL COMMENT '最后编辑时间',
  `filepath` varchar(255) DEFAULT NULL COMMENT '附件地址',
  `editor` varchar(255) DEFAULT NULL COMMENT '编者',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='课程项目';

