#
# Structure for table "tb_class"
#

CREATE TABLE `tb_class` (
  `cid` int(11) NOT NULL AUTO_INCREMENT,
  `classname` varchar(255) DEFAULT NULL,
  `teacher` varchar(255) DEFAULT NULL COMMENT '主讲师',
  `period` int(11) DEFAULT NULL COMMENT '课时',
  `introduction` varchar(255) DEFAULT NULL COMMENT '简介',
  `classinfo` text COMMENT '内容',
  `state` varchar(255) DEFAULT NULL COMMENT '状态',
  `starttime` varchar(255) DEFAULT NULL COMMENT '班级开课时间',
  `regtime` varchar(255) DEFAULT NULL COMMENT '报名时间',
  `regend` varchar(255) DEFAULT NULL COMMENT '报名结束时间',
  `coverpath` varchar(255) DEFAULT NULL COMMENT '班级封面地址',
  `price` varchar(255) DEFAULT NULL COMMENT '价格',
  `workis` varchar(255) DEFAULT NULL COMMENT '是否需要作品进行筛选',
  PRIMARY KEY (`cid`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='课程项目';

#
