CREATE TABLE `tb_enroll` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL COMMENT '姓名',
  `classes` varchar(255) DEFAULT NULL COMMENT '班级',
  `age` int(11) DEFAULT NULL COMMENT '年龄',
  `gender` varchar(255) DEFAULT NULL COMMENT '性别',
  `phonenumber` varchar(255) DEFAULT NULL COMMENT '联系方式',
  `qq` varchar(255) DEFAULT NULL,
  `email` varchar(255) DEFAULT NULL,
  `address` varchar(800) DEFAULT NULL COMMENT '家庭住址',
  `state` int(11) DEFAULT NULL COMMENT '状态',
  `basics` varchar(255) DEFAULT NULL COMMENT '基础情况',
  `info` varchar(255) DEFAULT NULL COMMENT '信息',
  `work` varchar(255) DEFAULT NULL COMMENT '作品(某些班级需要)',
  `account` varchar(255) DEFAULT NULL COMMENT '自己账号',
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='报名信息';

