#
# Structure for table "tb_work"
#

CREATE TABLE `tb_work` (
  `wid` int(11) NOT NULL AUTO_INCREMENT,
  `workname` varchar(255) DEFAULT NULL,
  `author` varchar(255) DEFAULT NULL COMMENT '作者',
  `message` varchar(255) DEFAULT NULL COMMENT '留语',
  `address` varchar(255) DEFAULT NULL COMMENT '所属课程',
  `picpath` varchar(255) DEFAULT NULL COMMENT '文件地址',
  PRIMARY KEY (`wid`)
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8 COMMENT='优秀作品';

